            Pair(InvalidOnionKey(ByteVector32("d2602c65fc331d6ae728331ae50e602f35929312ca7a951dc5ce250031b6b999")), OnionRoutingPacketSerializer(65).read(byteArrayOf(0) + ByteArray(33) + ByteArray(65) { 1 } + ByteArray(32))),
            Pair(
